mapper.insert:
/*kr.happy.study.adm.dao.ApprovalDao.insertapprovalcode*/


   	SELECT IFNULL(MAX(accno),0) +1
			  FROM tb_medicalinfo

	INSERT into tb_medicalinfo
			(
			  accno
			, regDate
			, dec_name
			, dec_birth
			, dec_address
			, copnm
			, coplicense
			, copaddress
			, type
			, divdtl
			, modelnm
			, divno
			, shape
			, property
			, usepurpose
			, usemethod
			, hints
			, manufacturer
			, etc
			, accyn
			, manager
			, managerphone
			, divcd
			) values (
			  #{selaccno}
			, now()
			, #{dec_name}
			, #{dec_birth}
			, #{dec_address}
			, #{copnm}
			, #{coplicense}
			, #{copaddress}
			, #{type}
			, #{divdtl}
			, #{modelnm}
			, #{divno}
			, #{shape}
			, #{property}
			, #{usepurpose}
			, #{usemethod}
			, #{hints}
			, #{manufacturer}
			, #{etc}
			, '0'
			, #{manager}
			, #{managerphone}
			, #{divcd}
			)

mapper.update:
/*kr.happy.study.adm.dao.ApprovalDao.updateApprovalCod*/
	UPDATE tb_medicalinfo SET
			accyn = #{accyn}
		,	accno = #{accno}
	WHERE accno = #{accno};

/*kr.happy.study.adm.dao.ApprovalDao.updateApprovalMedicalCod*/
	UPDATE tb_medicalinfo SET
			  accyn = #{accyn}
			, accno = #{accno}
			, regDate = #{regDate}
			, dec_name = #{dec_name}
			, dec_birth = #{dec_birth}
			, dec_address = #{dec_address}
			, copnm = #{copnm}
			, coplicense = #{coplicense}
			, copaddress = #{copaddress}
			, type = #{type}
			, divdtl = #{divdtl}
			, modelnm = #{modelnm}
			, divno = #{divno}
			, shape = #{shape}
			, property = #{property}
			, usepurpose = #{usepurpose}
			, usemethod = #{usemethod}
			, hints = #{hints}
			, manufacturer = #{manufacturer}
			, etc = #{etc}
			, manager = #{manager}
			, managerphone = #{managerphone}
			, divcd = #{divcd}
